/*


import styled from "styled-components";
import CoffeeCherry from '../images/coffeeImage/CoffeeCherry.jpg'


export const HomeCollectionPlant = styled.div`
background: linear-gradient(to right, rgba(0,0,0,0), rgba(0,0,0,0)), url(${CoffeeCherry});
  height: 40vh;
  display: flex;
  justify-content: space-between;
  resizeMode:"strech"
  width: auto;
`
//resizeMode:"contain"


