import React, { useState } from 'react';
//import {StyleSheet, TouchableOpacity, Image, View } from 'react-native';
//import coffeeHome from '../../images/coffeeHome.jpg'
import Navbar from '../../Navbar/Navbar';
import SearchCoffee from '../../SearchCoffee/SearchCoffee';
import SearchTea from '../../SearchTea/SearchTea';

import SocialMedia from '../../SocialMedia/SocialMedia';
import DisplayTime from '../../DisplayScreen/DisplayTime';

import './HomeScreenElements.css'
import axios from 'axios';

const HomeScreen = ( { navigation } ) => {

  const [posts, setPosts] = useState([]);

  const componentDidMount = () => {
    getBlogPost();
  }

  const getBlogPost = () => {
    axios.get('/api')
    .then((response) => {
      const data = response.data;
      setPosts({posts:data})
      console.log('Data has been received!!');
    })
    .catch(() => {
      alert('Error retriving data!!!');
    })
  }

  return (

      <div>
         {/* <Navbar /> */}
           <div className="container"> 
            
            <div className="row">
              <SearchCoffee />
              <SearchTea />
            </div>
          </div>
      
                
          <DisplayTime />

        <SocialMedia />
      </div>

  );
};


export default HomeScreen;
